$(document).ready(function(){
    $("#like-knowledge").mouseover(function(){
    	$("#like-knowledge").addClass("active");
    	$("#favorite-knowledge").removeClass("active");
    	$("#view-knowledge").removeClass("active");
  		$("#like-knowledge-list").css("display","");
  		$("#favorite-knowledge-list").css("display","none");
  		$("#view-knowledge-list").css("display","none");
	});
	$("#favorite-knowledge").mouseover(function(){
		$("#like-knowledge").removeClass("active");
    	$("#favorite-knowledge").addClass("active");
    	$("#view-knowledge").removeClass("active");
  		$("#favorite-knowledge-list").css("display","");
  		$("#view-knowledge-list").css("display","none");
  		$("#like-knowledge-list").css("display","none");
	});
	$("#view-knowledge").mouseover(function(){
		$("#like-knowledge").removeClass("active");
    	$("#favorite-knowledge").removeClass("active");
    	$("#view-knowledge").addClass("active");
  		$("#view-knowledge-list").css("display","");
  		$("#favorite-knowledge-list").css("display","none");
  		$("#like-knowledge-list").css("display","none");
	});
	$("#follow-topic").mouseover(function(){
		$("#follow-topic").addClass("active");
    	$("#knowledge-topic").removeClass("active");
  		$("#follow-topic-list").css("display","");
  		$("#knowledge-topic-list").css("display","none");
	});
	$("#knowledge-topic").mouseover(function(){
		$("#knowledge-topic").addClass("active");
    	$("#follow-topic").removeClass("active");
  		$("#knowledge-topic-list").css("display","");
  		$("#follow-topic-list").css("display","none");
	});
	$("#score-user").mouseover(function(){
		$("#knowledge-user").removeClass("active");
    	$("#score-user").addClass("active");
    	$("#browse-user").removeClass("active");
  		$("#score-user-list").css("display","");
  		$("#browse-user-list").css("display","none");
  		$("#knowledge-user-list").css("display","none");
	});
	$("#knowledge-user").mouseover(function(){
		$("#score-user").removeClass("active");
    	$("#knowledge-user").addClass("active");
    	$("#browse-user").removeClass("active");
  		$("#knowledge-user-list").css("display","");
  		$("#score-user-list").css("display","none");
  		$("#browse-user-list").css("display","none");
	});
	$("#browse-user").mouseover(function(){
		$("#score-user").removeClass("active");
    	$("#browse-user").addClass("active");
    	$("#knowledge-user").removeClass("active");
  		$("#browse-user-list").css("display","");
  		$("#score-user-list").css("display","none");
  		$("#knowledge-user-list").css("display","none");
	});
});
